import classes from "./Dialogs.module.css";

import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import React from "react";
import {sendMessageCreator, updateNewMessageTextCreator} from "../../redux/state";


const Dialogs = (props) => {


    let dialogElements = props._state.dialogsPage.dialogsData.map((dialog) => {
        return <DialogItem name={dialog.name} id={dialog.id}/>;
    });

    let messageElements = props._state.dialogsPage.messagesData.map((msg) => {
        return <Message message={msg.message}/>;
    });

    // let newMessageText = props.dialogsPage.
    let sendMessage = () => {
        props.dispatch(sendMessageCreator())
    }

    let newMessageElement = React.createRef();

    let newMessageChange = () => {
        let newMessage = newMessageElement.current.value
        let action = updateNewMessageTextCreator(newMessage)
        props.dispatch(action)
    }

    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>{dialogElements}</div>
            <div className={classes.messages}>
                <div>{messageElements}</div>
                <div className={classes.myMessage}>
                    <div><textarea onChange={newMessageChange} placeholder="Enter your message"/></div>
                    <div><button onClick={sendMessage}>Send</button></div>
                </div>
            </div>

        </div>
    );
};

export default Dialogs;
