import classes from "./Users.module.css"
import axios from "axios";
import userPhoto from "../../assets/images/users-vector-icon-png_260862.jpeg"
import React from "react";
import Users from "./Users";


class UsersAPIComponent extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(res => {
                this.props.setUsers(res.data.items)
                this.props.setTotalUsersCount(res.data.totalCount)
            })
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber)

        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
            .then(res => {
                this.props.setUsers(res.data.items)
            })
    }


    goToPage = (pageNumber) => {
        this.props.setCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
            .then(res => {
                this.props.setUsers(res.data.items)
            })
    }

    goOnePageBack = (pageNumber) => {
        if(this.props.currentPage !== 1){
            this.props.setCurrentPage(pageNumber)
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
                .then(res => {
                    this.props.setUsers(res.data.items)
                })
        }

    }
    pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize)
    goOnePageForward = (pageNumber) => {
        if(this.props.currentPage !== this.pagesCount){
            this.props.setCurrentPage(pageNumber)
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
                .then(res => {
                    this.props.setUsers(res.data.items)
                })
        }

    }


    render() {




        return (
            <Users/>
        )
    }
}

export default UsersAPIComponent
