import classes from "./Users.module.css";
import userPhoto from "../../assets/images/users-vector-icon-png_260862.jpeg";
import React from "react";


const Users = (props) => {


    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize)


    let pages = []

    for (let i = 1; i <= pagesCount; i++){
        pages.push(i)
    }

    let curPage = props.currentPage
    let curPageF = ((curPage - 5) < 0) ?  0  : curPage - 5 ;
    let curPageL = curPage + 4;
    let slicedPages = pages.slice( curPageF, curPageL);

    return(
        <div>
            <div className={classes.users}>
                <div className={classes.title}>
                    Users
                </div>
                <div className={classes.pageNumbers}>


                    <span className={classes.goFirstPage} onClick={() => this.goToPage(pages[0])}>&lt;&lt;</span>

                    <span className={classes.goToPage} onClick={() => this.goOnePageBack(this.props.currentPage-1)}>&lt;</span>
                    {slicedPages.map((p, index) => (
                        <span onClick={() => this.onPageChanged(p)} key={index} className={this.props.currentPage === p ? [classes.selectedPage, classes.pageNumber].join(' ') : classes.pageNumber}> {p} </span>
                    ))}
                    <span className={classes.goToPage} onClick={() => this.goOnePageForward(this.props.currentPage+1)}>&gt;</span>
                    <span className={classes.goLastPage} onClick={() => this.goToPage(pages[pages.length - 1])}>&gt;&gt;</span>
                </div>
                {
                    this.props.users.map(u =>
                        <div key={u.id} className={classes.user}>
                            <div className={classes.avatar}>
                                <div>
                                    <img src={u.photos.small != null || undefined ? u.photos.small : userPhoto} alt=""
                                         className={classes.userPhoto}/>
                                </div>
                                <div>
                                    {u.followed ? <button className={classes.unfollow} onClick={() => {
                                        this.props.unfollow(u.id)
                                    }}>Unfollow</button> : <button className={classes.follow} onClick={() => {
                                        this.props.follow(u.id)
                                    }}>Follow</button>}
                                </div>
                            </div>
                            <div className={classes.information}>
                                <div className={classes.info}>
                                    <div>{u.name}</div>
                                    <div>{u.status}</div>
                                </div>
                                <div className={classes.location}>
                                    <div>{"u.location.city"}</div>
                                    <div>{"u.location.country"}</div>
                                </div>
                            </div>
                        </div>
                    )
                }
            </div>
        </div>
    )
}

export default Users
