const FOLLOW = "FOLLOW"
const UNFOLLOW = "UNFOLLOW"
const SET_USERS = "SET_USERS"

let initialState = {
    users: [
        {
            id: 1,
            fullName: 'Asik',
            status: 'mob developer',
            location: {country: 'Kazakhstan', city: 'Kulsary'},
            isFollowed: true
        },
        {
            id: 2,
            fullName: 'Beka',
            status: 'backend',
            location: {country: 'Kazakhstan', city: 'Aktobe'},
            isFollowed: false
        },
        {
            id: 3,
            fullName: 'Azik',
            status: 'backend',
            location: {country: 'Kazakhstan', city: 'Uralsk'},
            isFollowed: true
        },
        {
            id: 4,
            fullName: 'Madi',
            status: 'frontend',
            location: {country: 'Kazakhstan', city: 'Aktobe'},
            isFollowed: true
        },
        {
            id: 5,
            fullName: 'Gorba',
            status: 'backend',
            location: {country: 'Kazakhstan', city: 'Aktobe'},
            isFollowed: false
        },
    ],
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId){
                        return {...u , isFollowed: true}
                    }
                    return u
                })
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId){
                        return {...u , isFollowed: false}
                    }
                    return u
                })
            }
        case SET_USERS:{
            return { ...state, users: [...state.users, action.users]}
        }
        default:
            return state
    }
}
export const followAC = (userId) => ({type: FOLLOW, userId})
export const unfollowAC = (userId) => ({type: UNFOLLOW, userId})
export const setUsersAC = (users) => ({type: SET_USERS, users})
export default usersReducer()
