const ADD_POST = "ADD-POST"
const UPDATE_NEW_POST_TEXT = "UPDATE-NEW-POST-TEXT"
const UPDATE_NEW_MESSAGE_TEXT = "UPDATE-NEW-MESSAGE-TEXT"
const SEND_MESSAGE = "SEND-MESSAGE"

let store = {
    _state: {
        profilePage: {
            postsData: [
                {id: 1, message: "Hello, whats up?", likes: 20},
                {id: 2, message: "It's my birthday today", likes: 30},
            ],
            newPostText: "",
        },
        dialogsPage: {
            dialogsData: [
                {id: 1, name: "Asik"},
                {id: 2, name: "Beka"},
                {id: 3, name: "Azik"},
                {id: 4, name: "Baur"},
            ],
            messagesData: [
                {id: 1, message: "Hello"},
                {id: 2, message: "Hi"},
                {id: 3, message: "Fuck you"},
                {id: 4, message: "Bitch"},
            ],
            newMessageText: ''
        },
        navBar: {
            friends: [
                {
                    id: 1,
                    name: "Asik",
                    avatar:
                        "https://w7.pngwing.com/pngs/236/25/png-transparent-computer-icons-avatar-friends-love-text-logo.png",
                },
                {
                    id: 2,
                    name: "Beka",
                    avatar:
                        "https://w7.pngwing.com/pngs/236/25/png-transparent-computer-icons-avatar-friends-love-text-logo.png",
                },
                {
                    id: 3,
                    name: "Azik",
                    avatar:
                        "https://w7.pngwing.com/pngs/236/25/png-transparent-computer-icons-avatar-friends-love-text-logo.png",
                },
                {
                    id: 4,
                    name: "Baur",
                    avatar:
                        "https://w7.pngwing.com/pngs/236/25/png-transparent-computer-icons-avatar-friends-love-text-logo.png",
                },
            ],
        },
    },
    _callSubscriber() {
    },

    getState() {
        return this._state
    },
    subscribe(observer) {
        this._callSubscriber = observer
    },

    dispatch(action) {
        if (action.type === ADD_POST) {
            let newPost = {
                id: 5,
                message: this._state.profilePage.newPostText,
                likes: 0,
            };
            this._state.profilePage.postsData.push(newPost);
            this._state.profilePage.newPostText = "";
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_POST_TEXT) {
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_MESSAGE_TEXT){
            this._state.dialogsPage.newMessageText = action.text
            this._callSubscriber(this._state)
        } else if (action.type === SEND_MESSAGE) {
            let newMessage = this._state.dialogsPage.newMessageText
            this._state.dialogsPage.messagesData.push({id: 4, message: newMessage})
            this._callSubscriber(this._state)
            this._state.dialogsPage.newMessageText = ''
        }
    }
}

export const addPostActionCreator = () => ({ type: ADD_POST })


export const updateNewPostTextActionCreator = (text) => (
    { type: UPDATE_NEW_POST_TEXT, text: text }
)

export const sendMessageCreator = () => ({ type: SEND_MESSAGE })
export const updateNewMessageTextCreator = () => ({ type: UPDATE_NEW_MESSAGE_TEXT })
window.store = store;

export default store
